			// 			};
			// 		}
			// 		return {
			// 			...item,
			// 			isExpanded: !item.isExpanded
			// 		};
			// 	})
			// };
			break;
		case 'SET_MENU':
			state = {
				...state,
				leftMenuItems: action.payload
			};
			break;
		default:
			return state;
	}
	return state;
};
