const initialAppState = {
	leftMenuOpen: false,
	leftMenuItems: []
};

export default (state = initialAppState, action) => {
	switch (action.type) {
		case 'TOGGLE_LEFT_MENU':
			state = {
				...state,
				leftMenuOpen: !state.leftMenuOpen
			};
			break;
		case 'TOGGLE_LEFT_MENU_ITEM':
			state = {
				...state,
				leftMenuItems: state.leftMenuItems.map(leftMenuItem => {
					if (leftMenuItem.name === action.payload) {
						return {
							...leftMenuItem,
							isExpanded: true
						};
					}
					return leftMenuItem;
				})
			};
			break;
		case 'SET_MENU':
			state = {
				...state,
				leftMenuItems: action.payload
			};
			break;
		default:
			return state;
	}
	return state;
};
