const initialAppState = {
	leftMenuOpen: false,
	leftMenuItems: []
};

export default (state = initialAppState, action) => {
	switch (action.type) {
		case 'TOGGLE_LEFT_MENU':
			state = {
				...state,
				leftMenuOpen: !state.leftMenuOpen
			};
			break;
		case 'TOGGLE_LEFT_MENU_ITEM':
			state = {
				...state,
				leftMenuItems: toggleLeftMenuItemByName(action.payload, state)
			};
			break;
		case 'SET_MENU':
			state = {
				...state,
				leftMenuItems: action.payload
			};
			break;
		default:
			return state;
	}
	return state;
};

/**
 * Toggles left menu item by its name
 * @param {*} name
 * @param {*} state
 * @returns {*} leftMenuItems with toggled isExpanded propertie for matched item by name
 */

//TODO: make this shit work!!!(make this function recursive)
function toggleLeftMenuItemByName(name, state) {
	return state.leftMenuItems.map(leftMenuItem => {
		if (leftMenuItem.items) {
			return toggleLeftMenuItemByName(leftMenuItem.items, state);
		} else if (leftMenuItem.name === name) {
			return {
				...leftMenuItem,
				isExpanded: true
			};
		}
		return {
			...leftMenuItem,
			isExpanded: false
		};
	});
}
